import {
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
  Text,
  Box,
} from "@chakra-ui/react";
import Data from "../data/faq.json";
import { colors } from "../themes";
import MinusIcon from "./icons/Minus";
import PlusIcon from "./icons/Plus";

const FaqAccordion = () => {
  return (
    <Accordion allowToggle allowMultiple>
      {Data.map((data) => (
        <AccordionItem key={data.id} pt={5} sx={{}}>
          {({ isExpanded }) => (
            <>
              <h2>
                <AccordionButton>
                  <Box
                    as="span"
                    flex="1"
                    fontWeight="bold"
                    fontSize={"20px"}
                    textAlign="left"
                    color={colors.darkPurple}
                  >
                    {data.question}
                  </Box>
                  {isExpanded ? <MinusIcon /> : <PlusIcon />}
                </AccordionButton>
              </h2>
              <AccordionPanel>
                <Text color={colors.grayishPurple}>{data.answer}</Text>
              </AccordionPanel>
            </>
          )}
        </AccordionItem>
      ))}
    </Accordion>
  );
};

export default FaqAccordion;
