import {
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
  Text,
} from "@chakra-ui/react";
import Data from "../data/faq.json";
import { colors } from "../themes";

const FaqAccordion = () => {
    return (
        <Accordion>
            {Data.map(data => (
                <AccordionItem key={data.id} py={10}>
                        <AccordionButton>
                            <Text
                                as="span"
                                flex="1"
                                textAlign="left"
                                color={colors.darkPurple}
                                fontWeight="700"
                            >
                                {data.question}
                            </Text>
                            <AccordionIcon />
                        </AccordionButton>
                    <AccordionPanel>
                        {data.answer}
                    </AccordionPanel>
                </AccordionItem>
            ))}
        </Accordion>
    )
}

export default FaqAccordion;
