import {
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
  Text,
} from "@chakra-ui/react";
import Data from "../data/faq.json";

const FaqAccordion = () => {
    return (
        <Accordion>
            {Data.map(data => (
                <AccordionItem key={data.id}>
                    <h2>
                        <AccordionButton>
                            <Text
                                as="span"
                                flex="1"
                                textAlign="left"
                            >
                                {data.question}
                            </Text>
                            <AccordionIcon />
                        </AccordionButton>
                    </h2>
                    <AccordionPanel pb={4}>
                        {data.answer}
                    </AccordionPanel>
                </AccordionItem>
            ))}
        </Accordion>
    )
}

export default FaqAccordion;
